/* VideoHub - Custom Styles */

/* Global Variables */
:root {
    --primary-color: #0d6efd;
    --secondary-color: #6c757d;
    --success-color: #198754;
    --danger-color: #dc3545;
    --warning-color: #ffc107;
    --info-color: #0dcaf0;
    --light-color: #f8f9fa;
    --dark-color: #212529;
    --border-radius: 0.375rem;
    --box-shadow: 0 0.125rem 0.25rem rgba(0, 0, 0, 0.075);
    --box-shadow-lg: 0 1rem 3rem rgba(0, 0, 0, 0.175);
}

/* General Styles */
body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
    line-height: 1.6;
    color: #212529;
}

.auth-bg {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
}

/* Hero Section */
.hero-section {
    background: linear-gradient(135deg, var(--primary-color) 0%, #0056b3 100%);
    border-radius: var(--border-radius);
}

.bg-gradient {
    background: linear-gradient(135deg, var(--primary-color) 0%, #0056b3 100%);
}

/* Cards */
.card {
    border: none;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
    transition: all 0.3s ease;
}

.card:hover {
    box-shadow: var(--box-shadow-lg);
    transform: translateY(-2px);
}

.card-header {
    background-color: #f8f9fa;
    border-bottom: 1px solid #dee2e6;
    font-weight: 600;
}

/* Border Left Colors for Cards */
.border-left-primary {
    border-left: 0.25rem solid var(--primary-color) !important;
}

.border-left-success {
    border-left: 0.25rem solid var(--success-color) !important;
}

.border-left-info {
    border-left: 0.25rem solid var(--info-color) !important;
}

.border-left-warning {
    border-left: 0.25rem solid var(--warning-color) !important;
}

/* Sidebar */
.sidebar {
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    z-index: 100;
    padding: 48px 0 0;
    box-shadow: inset -1px 0 0 rgba(0, 0, 0, 0.1);
}

.sidebar .nav-link {
    color: #333;
    padding: 0.75rem 1rem;
    border-radius: 0;
    transition: all 0.3s ease;
}

.sidebar .nav-link:hover {
    color: var(--primary-color);
    background-color: rgba(0, 0, 0, 0.05);
}

.sidebar .nav-link.active {
    color: var(--primary-color);
    background-color: rgba(13, 110, 253, 0.1);
    border-right: 3px solid var(--primary-color);
}

/* Video Thumbnails */
.video-thumbnail {
    position: relative;
    overflow: hidden;
    border-radius: var(--border-radius);
    background-color: #000;
    aspect-ratio: 16/9;
}

.video-thumbnail img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.3s ease;
}

.video-thumbnail:hover img {
    transform: scale(1.05);
}

.video-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.5);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: opacity 0.3s ease;
    color: white;
}

.video-thumbnail:hover .video-overlay {
    opacity: 1;
}

.video-duration {
    position: absolute;
    bottom: 8px;
    right: 8px;
    background: rgba(0, 0, 0, 0.8);
    color: white;
    padding: 2px 6px;
    border-radius: 3px;
    font-size: 0.75rem;
}

.video-price {
    position: absolute;
    top: 8px;
    left: 8px;
    background: var(--success-color);
    color: white;
    padding: 4px 8px;
    border-radius: var(--border-radius);
    font-size: 0.75rem;
    font-weight: 600;
}

/* Video Player Styles */
.video-player-body {
    background-color: #000;
    color: #fff;
}

.video-player-container {
    background-color: #000;
    border-radius: var(--border-radius);
    overflow: hidden;
}

.video-wrapper {
    position: relative;
    width: 100%;
    height: 0;
    padding-bottom: 56.25%; /* 16:9 aspect ratio */
    background-color: #000;
}

.video-player {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border: none;
    outline: none;
}

.video-loading-overlay,
.video-error-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    z-index: 10;
}

/* Modal Video Player Styles */
#videoPlayerModal .modal-xl {
    max-width: 95%;
}

#videoPlayerModal .video-wrapper {
    height: 400px;
    padding-bottom: 0;
}

#videoPlayerModal .video-player {
    position: relative;
    width: 100%;
    height: 100%;
}

.related-video-item {
    cursor: pointer;
    padding: 8px;
    border-radius: 4px;
    transition: background-color 0.2s;
}

.related-video-item:hover {
    background-color: #f8f9fa;
}

.cursor-pointer {
    cursor: pointer;
}

.custom-video-controls {
    background: rgba(255, 255, 255, 0.1);
    padding: 1rem;
    border-radius: 0 0 var(--border-radius) var(--border-radius);
}

/* Video Cards */
.video-card {
    transition: all 0.3s ease;
    border: none;
    box-shadow: var(--box-shadow);
}

.video-card:hover {
    box-shadow: var(--box-shadow-lg);
    transform: translateY(-5px);
}

.video-card .card-body {
    padding: 1rem;
}

.video-title {
    font-size: 1rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
    color: #212529;
    text-decoration: none;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

.video-title:hover {
    color: var(--primary-color);
}

.video-creator {
    color: #6c757d;
    font-size: 0.875rem;
    margin-bottom: 0.5rem;
}

.video-stats {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.75rem;
    color: #6c757d;
}

/* Authentication Forms */
.auth-card {
    border: none;
    box-shadow: var(--box-shadow-lg);
    border-radius: var(--border-radius);
}

/* Password Strength Indicator */
.password-strength {
    margin-top: 0.5rem;
}

.strength-bar {
    height: 4px;
    background-color: #e9ecef;
    border-radius: 2px;
    transition: background-color 0.3s ease;
}

.strength-bar.weak {
    background-color: var(--danger-color);
}

.strength-bar.fair {
    background-color: var(--warning-color);
}

.strength-bar.good {
    background-color: var(--info-color);
}

.strength-bar.strong {
    background-color: var(--success-color);
}

/* Buttons */
.btn {
    border-radius: var(--border-radius);
    font-weight: 500;
    transition: all 0.3s ease;
}

.btn:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
}

.btn-group .btn:hover {
    transform: none;
}

/* Tables */
.table th {
    border-top: none;
    font-weight: 600;
    color: #495057;
    background-color: #f8f9fa;
}

.table-responsive {
    border-radius: var(--border-radius);
    overflow: hidden;
}

/* Pagination */
.pagination .page-link {
    border-radius: var(--border-radius);
    margin: 0 2px;
    border: 1px solid #dee2e6;
    color: #495057;
}

.pagination .page-item.active .page-link {
    background-color: var(--primary-color);
    border-color: var(--primary-color);
}

/* Form Controls */
.form-control:focus,
.form-select:focus {
    border-color: var(--primary-color);
    box-shadow: 0 0 0 0.2rem rgba(13, 110, 253, 0.25);
}

/* Alerts */
.alert {
    border: none;
    border-radius: var(--border-radius);
}

/* Demo Notice */
.demo-notice {
    border-radius: 0;
}

/* Loading States */
.loading-spinner {
    display: inline-block;
    width: 20px;
    height: 20px;
    border: 3px solid rgba(255, 255, 255, 0.3);
    border-radius: 50%;
    border-top-color: #fff;
    animation: spin 1s ease-in-out infinite;
}

@keyframes spin {
    to { transform: rotate(360deg); }
}

/* Chart Container */
.chart-container {
    position: relative;
    height: 400px;
}

/* Progress Bars */
.progress {
    height: 8px;
    border-radius: 4px;
    background-color: #e9ecef;
}

.progress-bar {
    border-radius: 4px;
    transition: width 0.3s ease;
}

/* Modals */
.modal-content {
    border: none;
    border-radius: var(--border-radius);
    box-shadow: var(--box-shadow-lg);
}

.modal-header {
    border-bottom: 1px solid #dee2e6;
}

.modal-footer {
    border-top: 1px solid #dee2e6;
}

/* User Avatar */
.user-avatar {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    object-fit: cover;
}

/* Comments */
.comment-item {
    border-bottom: 1px solid #e9ecef;
    padding: 1rem 0;
}

.comment-item:last-child {
    border-bottom: none;
}

.comment-avatar {
    width: 48px;
    height: 48px;
    border-radius: 50%;
    background-color: #e9ecef;
    display: flex;
    align-items: center;
    justify-content: center;
    color: #6c757d;
}

/* Related Videos */
.related-videos-container {
    max-height: 400px;
    overflow-y: auto;
}

.related-video-item {
    display: flex;
    align-items: flex-start;
    padding: 0.75rem;
    margin-bottom: 0.5rem;
    border-radius: 8px;
    text-decoration: none;
    color: inherit;
    transition: all 0.3s ease;
    background-color: #f8f9fa;
    border: 1px solid #e9ecef;
}

.related-video-item:hover {
    background-color: #e9ecef;
    color: inherit;
    transform: translateY(-1px);
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.related-video-item:last-child {
    margin-bottom: 0;
}

.related-video-thumbnail {
    width: 80px;
    height: 45px;
    background-color: #000;
    border-radius: 6px;
    margin-right: 0.75rem;
    flex-shrink: 0;
    object-fit: cover;
    position: relative;
}

.related-video-thumbnail::after {
    content: '\f04b';
    font-family: 'Font Awesome 6 Free';
    font-weight: 900;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: white;
    opacity: 0;
    transition: opacity 0.3s ease;
    font-size: 12px;
}

.related-video-item:hover .related-video-thumbnail::after {
    opacity: 1;
}

.related-video-info {
    flex: 1;
    min-width: 0;
}

.related-video-title {
    font-size: 0.85rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
    line-height: 1.3;
    color: #212529;
}

.related-video-creator {
    font-size: 0.75rem;
    color: #6c757d;
    margin-bottom: 0.125rem;
}

.related-video-duration {
    font-size: 0.7rem;
    color: #28a745;
    font-weight: 500;
}

/* Enhanced Modal Sidebar */
#videoPlayerModal .modal-xl .col-lg-4 {
    border-left: 2px solid #e9ecef;
}

#videoPlayerModal .progress {
    background-color: #e9ecef;
    border-radius: 10px;
}

#videoPlayerModal .progress-bar {
    border-radius: 10px;
    background: linear-gradient(90deg, #007bff 0%, #0056b3 100%);
}

#videoPlayerModal .form-control {
    resize: none;
    transition: all 0.3s ease;
}

#videoPlayerModal .form-control:focus {
    border-color: #007bff;
    box-shadow: 0 0 0 0.2rem rgba(0, 123, 255, 0.25);
    background-color: white;
}

/* Custom scrollbar for related videos */
.related-videos-container::-webkit-scrollbar {
    width: 6px;
}

.related-videos-container::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 3px;
}

.related-videos-container::-webkit-scrollbar-thumb {
    background: #c1c1c1;
    border-radius: 3px;
}

.related-videos-container::-webkit-scrollbar-thumb:hover {
    background: #a8a8a8;
}

/* Responsive Design */
@media (max-width: 768px) {
    .sidebar {
        position: static;
        height: auto;
        padding: 0;
    }

    .video-wrapper {
        padding-bottom: 75%; /* Adjust for mobile */
    }

    .custom-video-controls .row {
        flex-direction: column;
        gap: 1rem;
    }

    .video-stats {
        flex-direction: column;
        align-items: flex-start;
        gap: 0.5rem;
    }

    .related-video-item {
        flex-direction: column;
        text-align: center;
    }

    .related-video-thumbnail {
        width: 100%;
        height: auto;
        margin-right: 0;
        margin-bottom: 0.5rem;
    }
}

/* Dark Theme Support */
@media (prefers-color-scheme: dark) {
    .video-player-body {
        background-color: #121212;
        color: #ffffff;
    }

    .video-player-body .card {
        background-color: #1e1e1e;
        color: #ffffff;
    }

    .video-player-body .card-header {
        background-color: #2d2d2d;
        border-bottom-color: #404040;
    }
}

/* Print Styles */
@media print {
    .navbar,
    .sidebar,
    .btn,
    .modal {
        display: none !important;
    }

    .container-fluid {
        margin: 0;
        padding: 0;
    }

    .card {
        box-shadow: none;
        border: 1px solid #dee2e6;
    }
}

/* Accessibility */
.sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
}

/* Focus styles for keyboard navigation */
.btn:focus,
.form-control:focus,
.form-select:focus {
    outline: 2px solid var(--primary-color);
    outline-offset: 2px;
}

/* High contrast mode support */
@media (prefers-contrast: high) {
    .card {
        border: 2px solid #000;
    }

    .btn {
        border-width: 2px;
    }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

