}

/* Buttons */
.btn-modern {
    border-radius: var(--border-radius-sm);
    font-weight: 500;
    padding: 0.75rem 1.5rem;
    transition: all 0.2s ease;
    border: none;
}

.btn-modern:hover {
    transform: translateY(-1px);
    box-shadow: var(--box-shadow-md);
}

.card-header {
    background-color: #f8f9fa;
    border-bottom: 1px solid #dee2e6;
