}

.sidebar .nav-link.active {
    color: var(--primary-color);
    background-color: rgba(79, 70, 229, 0.1);
    font-weight: 600;
}

.sidebar .nav-link i {
    width: 1.5rem;
}

/* Avatar */
.avatar {
    width: 2.5rem;
    height: 2.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
