}

/* Utility classes for custom colors */
.bg-gray-50 { background-color: var(--gray-50) !important; }
.bg-gray-100 { background-color: var(--gray-100) !important; }
.bg-gray-200 { background-color: var(--gray-200) !important; }
.text-gray-500 { color: var(--gray-500) !important; }
.text-gray-600 { color: var(--gray-600) !important; }
.text-gray-700 { color: var(--gray-700) !important; }
.text-gray-800 { color: var(--gray-800) !important; }

/* Modern Utility Classes */
.rounded-xl { border-radius: 1rem !important; }
.rounded-2xl { border-radius: 1.5rem !important; }
.rounded-3xl { border-radius: 2rem !important; }

.shadow-soft { box-shadow: 0 2px 15px rgba(0, 0, 0, 0.08) !important; }
.shadow-medium { box-shadow: 0 4px 25px rgba(0, 0, 0, 0.12) !important; }
.shadow-large { box-shadow: 0 8px 40px rgba(0, 0, 0, 0.15) !important; }
.shadow-glow { box-shadow: 0 0 30px rgba(79, 70, 229, 0.3) !important; }

.text-gradient-primary {
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    font-weight: 700;
}

.text-gradient-success {
    background: linear-gradient(135deg, var(--success-color), #20c997);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    font-weight: 700;
}

.hover-scale:hover {
    transform: scale(1.05);
    transition: transform 0.3s ease;
}

.hover-rotate:hover {
    transform: rotate(5deg);
    transition: transform 0.3s ease;
}

.animate-glow {
    animation: glow 2s infinite;
}

.animate-shimmer {
    background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
}

.border-gradient {
    border: 2px solid;
    border-image: linear-gradient(45deg, var(--primary-color), var(--secondary-color)) 1;
}

.bg-mesh {
    background: 
        radial-gradient(circle at 20% 50%, rgba(79, 70, 229, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 80% 20%, rgba(99, 102, 241, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 40% 80%, rgba(139, 92, 246, 0.1) 0%, transparent 50%);
}

/* Video Thumbnails */
.video-thumbnail {
    position: relative;
    overflow: hidden;
    border-radius: var(--border-radius);
    background-color: #000;
    aspect-ratio: 16/9;
}

.video-thumbnail img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.3s ease;
}

.video-thumbnail:hover img {
    transform: scale(1.05);
}

.video-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.5);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: opacity 0.3s ease;
    color: white;
}

.video-thumbnail:hover .video-overlay {
    opacity: 1;
}

.video-duration {
    position: absolute;
    bottom: 8px;
    right: 8px;
    background: rgba(0, 0, 0, 0.8);
    color: white;
    padding: 2px 6px;
    border-radius: 3px;
    font-size: 0.75rem;
}

.video-price {
    position: absolute;
    top: 8px;
    left: 8px;
    background: var(--success-color);
    color: white;
    padding: 4px 8px;
    border-radius: var(--border-radius);
    font-size: 0.75rem;
    font-weight: 600;
}

/* Video Player Styles */
.video-player-body {
    background: linear-gradient(135deg, #0f0f23 0%, #1a1a2e 100%);
    color: #fff;
}

.video-player-container {
    background: linear-gradient(135deg, #000 0%, #1a1a2e 100%);
    border-radius: 20px;
    overflow: hidden;
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.3);
    position: relative;
}

.video-player-container::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border-radius: 20px;
    padding: 2px;
    background: linear-gradient(45deg, rgba(79, 70, 229, 0.5), rgba(99, 102, 241, 0.5));
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask-composite: exclude;
    z-index: 1;
}

.video-wrapper {
    position: relative;
    width: 100%;
    height: 0;
    padding-bottom: 56.25%; /* 16:9 aspect ratio */
    background-color: #000;
}

.video-player {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border: none;
    outline: none;
}

.video-loading-overlay,
.video-error-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    z-index: 10;
}

/* Modal Video Player Styles */
#videoPlayerModal .modal-xl {
    max-width: 95%;
}

#videoPlayerModal .video-wrapper {
    height: 400px;
    padding-bottom: 0;
}

#videoPlayerModal .video-player {
    position: relative;
    width: 100%;
    height: 100%;
}

.related-video-item {
    cursor: pointer;
    padding: 8px;
    border-radius: 4px;
    transition: background-color 0.2s;
}

.related-video-item:hover {
    background-color: #f8f9fa;
}

.cursor-pointer {
    cursor: pointer;
}

.custom-video-controls {
    background: rgba(255, 255, 255, 0.1);
