    border-radius: 0 0 var(--border-radius) var(--border-radius);
}

/* Video Cards */
.video-card {
    transition: all 0.3s ease;
    border: none;
    box-shadow: var(--box-shadow);
}

.video-card:hover {
    box-shadow: var(--box-shadow-lg);
    transform: translateY(-5px);
}

.video-card .card-body {
    padding: 1rem;
}

.video-title {
    font-size: 1rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
    color: #212529;
