}

.related-video-thumbnail::after {
    content: '\f04b';
    font-family: 'Font Awesome 6 Free';
    font-weight: 900;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: white;
    opacity: 0;
    transition: opacity 0.3s ease;
    font-size: 12px;
}

.related-video-item:hover .related-video-thumbnail::after {
    opacity: 1;
}

.related-video-info {
    flex: 1;
    min-width: 0;
}

.related-video-title {
    font-size: 0.85rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
    line-height: 1.3;
    color: #212529;
}

.related-video-creator {
    font-size: 0.75rem;
    color: #6c757d;
    margin-bottom: 0.125rem;
}

.related-video-duration {
    font-size: 0.7rem;
    color: #28a745;
    font-weight: 500;
}

/* Enhanced Modal Sidebar */
#videoPlayerModal .modal-xl .col-lg-4 {
    border-left: 2px solid #e9ecef;
}

#videoPlayerModal .progress {
    background-color: #e9ecef;
    border-radius: 10px;
}

#videoPlayerModal .progress-bar {
    border-radius: 10px;
    background: linear-gradient(90deg, #007bff 0%, #0056b3 100%);
}

#videoPlayerModal .form-control {
    resize: none;
    transition: all 0.3s ease;
}

#videoPlayerModal .form-control:focus {
    border-color: #007bff;
    box-shadow: 0 0 0 0.2rem rgba(0, 123, 255, 0.25);
    background-color: white;
}

/* Custom scrollbar styles */
.related-videos-container::-webkit-scrollbar,
.custom-scrollbar::-webkit-scrollbar {
    width: 8px;
}

.related-videos-container::-webkit-scrollbar-track,
.custom-scrollbar::-webkit-scrollbar-track {
    background: linear-gradient(to bottom, #f8f9fa, #e9ecef);
    border-radius: 4px;
}

.related-videos-container::-webkit-scrollbar-thumb,
.custom-scrollbar::-webkit-scrollbar-thumb {
    background: linear-gradient(to bottom, var(--primary-color), var(--primary-dark));
    border-radius: 4px;
    transition: all 0.3s ease;
}

.related-videos-container::-webkit-scrollbar-thumb:hover,
.custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(to bottom, var(--primary-dark), var(--secondary-color));
    transform: scale(1.1);
}

/* Modern scrollbar for all containers */
*::-webkit-scrollbar {
    width: 6px;
    height: 6px;
}

*::-webkit-scrollbar-track {
    background: rgba(0, 0, 0, 0.05);
    border-radius: 3px;
}

*::-webkit-scrollbar-thumb {
    background: linear-gradient(45deg, var(--primary-color), var(--secondary-color));
    border-radius: 3px;
}

*::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(45deg, var(--primary-dark), var(--primary-color));
}

/* Responsive Design */
@media (max-width: 768px) {
    .sidebar {
        position: static;
        height: auto;
        padding: 0;
    }

    .video-wrapper {
        padding-bottom: 75%; /* Adjust for mobile */
    }

    .custom-video-controls .row {
        flex-direction: column;
        gap: 1rem;
    }

    .video-stats {
        flex-direction: column;
        align-items: flex-start;
        gap: 0.5rem;
    }

    .related-video-item {
        flex-direction: column;
        text-align: center;
    }

    .related-video-thumbnail {
        width: 100%;
        height: auto;
        margin-right: 0;
        margin-bottom: 0.5rem;
    }
}

/* Dark Theme Support */
@media (prefers-color-scheme: dark) {
    .video-player-body {
        background-color: #121212;
        color: #ffffff;
    }

    .video-player-body .card {
        background-color: #1e1e1e;
        color: #ffffff;
    }

    .video-player-body .card-header {
        background-color: #2d2d2d;
        border-bottom-color: #404040;
    }
}

/* Print Styles */
@media print {
    .navbar,
    .sidebar,
    .btn,
    .modal {
        display: none !important;
    }

    .container-fluid {
        margin: 0;
        padding: 0;
    }

    .card {
        box-shadow: none;
        border: 1px solid #dee2e6;
    }
}

/* Accessibility */
.sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
}

/* Focus styles for keyboard navigation */
.btn:focus,
.form-control:focus,


/* Interactive Elements & Micro-interactions */
.interactive-card {
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    cursor: pointer;
    position: relative;
    overflow: hidden;
}

.interactive-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(45deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    transform: translateX(-100%);
    transition: transform 0.6s ease;
}

.interactive-card:hover::before {
    transform: translateX(100%);
}

.ripple-effect {
    position: relative;
    overflow: hidden;
    cursor: pointer;
}

.ripple-effect::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.3);
    transform: translate(-50%, -50%);
    transition: width 0.6s, height 0.6s;
}

.ripple-effect:active::after {
    width: 300px;
    height: 300px;
}

/* Loading Skeleton Improvements */
.skeleton-text {
    height: 1em;
    background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
    background-size: 200% 100%;
    animation: skeleton-loading 1.5s infinite;
    border-radius: 4px;
    margin: 0.25em 0;
}

.skeleton-circle {
    border-radius: 50%;
    background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
    background-size: 200% 100%;
    animation: skeleton-loading 1.5s infinite;
}

@keyframes skeleton-loading {
    0% { background-position: 200% 0; }
    100% { background-position: -200% 0; }
}

/* Status Indicators */
.status-indicator {
    position: relative;
    display: inline-block;
}

.status-indicator::after {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    width: 8px;
    height: 8px;
    border-radius: 50%;
    border: 2px solid #fff;
}

.status-online::after {
    background: #22c55e;
    animation: pulse-green 2s infinite;
}

.status-offline::after {
    background: #ef4444;
}

.status-busy::after {
    background: #f59e0b;
}

@keyframes pulse-green {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
}

/* Modern Form Enhancements */
.form-floating-modern {
    position: relative;
}

.form-floating-modern input {
    border: 2px solid #e5e7eb;
    border-radius: 12px;
    padding: 1rem;
    font-size: 1rem;
    transition: all 0.3s ease;
    background: rgba(255, 255, 255, 0.9);
}

.form-floating-modern input:focus {
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(79, 70, 229, 0.1);
    transform: translateY(-2px);
}

.form-floating-modern label {
    position: absolute;
    top: 1rem;
    left: 1rem;
    color: #6b7280;
    transition: all 0.3s ease;
    pointer-events: none;
    background: rgba(255, 255, 255, 0.9);
    padding: 0 0.5rem;
}

.form-floating-modern input:focus + label,
.form-floating-modern input:not(:placeholder-shown) + label {
    top: -0.5rem;
    font-size: 0.875rem;
    color: var(--primary-color);
    font-weight: 600;
}

/* Notification Enhancements */
.notification-badge {
    position: relative;
    display: inline-block;
}

.notification-badge::after {
    content: '';
    position: absolute;
    top: -2px;
    right: -2px;
    width: 8px;
    height: 8px;
    background: #ef4444;
    border-radius: 50%;
    border: 2px solid #fff;
    animation: pulse-notification 2s infinite;
}

@keyframes pulse-notification {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.2); }
}

/* Tooltip Enhancements */
.tooltip-modern {
    position: relative;
    cursor: help;
}

.tooltip-modern::before {
    content: attr(data-tooltip);
    position: absolute;
    bottom: 100%;
    left: 50%;
    transform: translateX(-50%);
    background: rgba(0, 0, 0, 0.9);
    color: white;
    padding: 0.5rem 1rem;
    border-radius: 8px;
    font-size: 0.875rem;
    white-space: nowrap;
    opacity: 0;
    visibility: hidden;
    transition: all 0.3s ease;
    z-index: 1000;
}

.tooltip-modern::after {
    content: '';
    position: absolute;
    bottom: 100%;
    left: 50%;
    transform: translateX(-50%);
    width: 0;
    height: 0;
    border-left: 6px solid transparent;
    border-right: 6px solid transparent;
    border-top: 6px solid rgba(0, 0, 0, 0.9);
    opacity: 0;
    visibility: hidden;
    transition: all 0.3s ease;
}

.tooltip-modern:hover::before,
.tooltip-modern:hover::after {
    opacity: 1;
    visibility: visible;
    bottom: calc(100% + 5px);
}


.form-select:focus {
    outline: 2px solid var(--primary-color);
    outline-offset: 2px;
}

/* High contrast mode support */
@media (prefers-contrast: high) {
    .card {
        border: 2px solid #000;
    }

    .btn {
        border-width: 2px;
    }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

