/* VideoHub - Enhanced Modern Design System */

/* Advanced Color Palette & Design Tokens */
:root {
    /* Primary Brand Colors */
    --primary-50: #f0f4ff;
    --primary-100: #e0e9ff;
    --primary-200: #c7d6fe;
    --primary-300: #a5b4fc;
    --primary-400: #818cf8;
    --primary-500: #6366f1;
    --primary-600: #4f46e5;
    --primary-700: #4338ca;
    --primary-800: #3730a3;
    --primary-900: #312e81;

    /* Secondary Palette */
    --secondary-50: #fdf4ff;
    --secondary-100: #fae8ff;
    --secondary-200: #f5d0fe;
    --secondary-300: #f0abfc;
    --secondary-400: #e879f9;
    --secondary-500: #d946ef;
    --secondary-600: #c026d3;
    --secondary-700: #a21caf;
    --secondary-800: #86198f;
    --secondary-900: #701a75;

    /* Success Colors */
    --success-50: #ecfdf5;
    --success-100: #d1fae5;
    --success-200: #a7f3d0;
    --success-300: #6ee7b7;
    --success-400: #34d399;
    --success-500: #10b981;
    --success-600: #059669;
    --success-700: #047857;
    --success-800: #065f46;
    --success-900: #064e3b;

    /* Warning Colors */
    --warning-50: #fffbeb;
    --warning-100: #fef3c7;
    --warning-200: #fde68a;
    --warning-300: #fcd34d;
    --warning-400: #fbbf24;
    --warning-500: #f59e0b;
    --warning-600: #d97706;
    --warning-700: #b45309;
    --warning-800: #92400e;
    --warning-900: #78350f;

    /* Danger Colors */
    --danger-50: #fef2f2;
    --danger-100: #fee2e2;
    --danger-200: #fecaca;
    --danger-300: #fca5a5;
    --danger-400: #f87171;
    --danger-500: #ef4444;
    --danger-600: #dc2626;
    --danger-700: #b91c1c;
    --danger-800: #991b1b;
    --danger-900: #7f1d1d;

    /* Neutral Grays */
    --gray-50: #f9fafb;
    --gray-100: #f3f4f6;
    --gray-200: #e5e7eb;
    --gray-300: #d1d5db;
    --gray-400: #9ca3af;
    --gray-500: #6b7280;
    --gray-600: #4b5563;
    --gray-700: #374151;
    --gray-800: #1f2937;
    --gray-900: #111827;

    /* Surface Colors */
    --surface-primary: #ffffff;
    --surface-secondary: #f8fafc;
    --surface-tertiary: #f1f5f9;
    --surface-dark: #0f172a;
    --surface-darker: #020617;

    /* Border & Shadows */
    --border-light: #e2e8f0;
    --border-medium: #cbd5e1;
    --border-dark: #64748b;

    /* Advanced Shadows */
    --shadow-xs: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
    --shadow-sm: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
    --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
    --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
    --shadow-2xl: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
    --shadow-inner: inset 0 2px 4px 0 rgba(0, 0, 0, 0.06);

    /* Colored Shadows */
    --shadow-primary: 0 4px 14px 0 rgba(99, 102, 241, 0.2);
    --shadow-secondary: 0 4px 14px 0 rgba(217, 70, 239, 0.2);
    --shadow-success: 0 4px 14px 0 rgba(16, 185, 129, 0.2);
    --shadow-warning: 0 4px 14px 0 rgba(245, 158, 11, 0.2);
    --shadow-danger: 0 4px 14px 0 rgba(239, 68, 68, 0.2);

    /* Border Radius */
    --radius-xs: 0.125rem;
    --radius-sm: 0.25rem;
    --radius-md: 0.375rem;
    --radius-lg: 0.5rem;
    --radius-xl: 0.75rem;
    --radius-2xl: 1rem;
    --radius-3xl: 1.5rem;
    --radius-full: 9999px;

    /* Typography */
    --font-sans: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
    --font-mono: 'JetBrains Mono', 'Fira Code', 'Consolas', monospace;

    /* Transitions */
    --transition-fast: 150ms cubic-bezier(0.4, 0, 0.2, 1);
    --transition-base: 300ms cubic-bezier(0.4, 0, 0.2, 1);
    --transition-slow: 500ms cubic-bezier(0.4, 0, 0.2, 1);

    /* Z-Index Scale */
    --z-dropdown: 1000;
    --z-sticky: 1020;
    --z-fixed: 1030;
    --z-modal-backdrop: 1040;
    --z-modal: 1050;
    --z-popover: 1060;
    --z-tooltip: 1070;
}

/* Enhanced Base Styles */
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

html {
    scroll-behavior: smooth;
    font-size: 16px;
}

body {
    font-family: var(--font-sans);
    line-height: 1.6;
    color: var(--gray-800);
    background: linear-gradient(135deg, var(--gray-50) 0%, var(--primary-50) 100%);
    min-height: 100vh;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

/* Advanced Gradient Backgrounds */
.bg-gradient-primary {
    background: linear-gradient(135deg, var(--primary-600) 0%, var(--primary-700) 50%, var(--primary-800) 100%);
}

.bg-gradient-secondary {
    background: linear-gradient(135deg, var(--secondary-500) 0%, var(--secondary-600) 50%, var(--secondary-700) 100%);
}

.bg-gradient-success {
    background: linear-gradient(135deg, var(--success-500) 0%, var(--success-600) 50%, var(--success-700) 100%);
}

.bg-gradient-mesh {
    background: 
        radial-gradient(at 40% 20%, var(--primary-400) 0px, transparent 50%),
        radial-gradient(at 80% 0%, var(--secondary-400) 0px, transparent 50%),
        radial-gradient(at 0% 50%, var(--success-400) 0px, transparent 50%),
        radial-gradient(at 80% 50%, var(--warning-400) 0px, transparent 50%),
        radial-gradient(at 0% 100%, var(--danger-400) 0px, transparent 50%),
        radial-gradient(at 80% 100%, var(--primary-400) 0px, transparent 50%),
        radial-gradient(at 0% 0%, var(--secondary-400) 0px, transparent 50%);
    background-size: 300% 300%;
    animation: mesh-movement 20s ease infinite;
}

@keyframes mesh-movement {
    0%, 100% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
}

/* Enhanced Card System */
.card {
    background: var(--surface-primary);
    border: 1px solid var(--border-light);
    border-radius: var(--radius-xl);
    box-shadow: var(--shadow-sm);
    transition: all var(--transition-base);
    overflow: hidden;
    position: relative;
}

.card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, var(--primary-500), var(--secondary-500), var(--success-500));
    opacity: 0;
    transition: opacity var(--transition-base);
}

.card:hover {
    /* transform: translateY(-4px);
    box-shadow: var(--shadow-xl);
    border-color: var(--primary-200); */
}

.card:hover::before {
    opacity: 1;
}

.card-premium {
    background: linear-gradient(135deg, var(--surface-primary) 0%, var(--primary-50) 100%);
    border: 2px solid var(--primary-200);
    box-shadow: var(--shadow-primary);
}

.card-glass {
    background: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(20px) saturate(180%);
    -webkit-backdrop-filter: blur(20px) saturate(180%);
    border: 1px solid rgba(255, 255, 255, 0.3);
    box-shadow: var(--shadow-lg);
}

.card-gradient {
    background: linear-gradient(135deg, var(--primary-600) 0%, var(--secondary-600) 100%);
    color: white;
    border: none;
}

/* Advanced Button System */
.btn {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    padding: 0.75rem 1.5rem;
    font-family: var(--font-sans);
    font-size: 0.875rem;
    font-weight: 600;
    line-height: 1;
    text-decoration: none;
    border: 2px solid transparent;
    border-radius: var(--radius-lg);
    cursor: pointer;
    transition: all var(--transition-base);
    position: relative;
    overflow: hidden;
    letter-spacing: 0.025em;
}

.btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left var(--transition-slow);
}

.btn:hover::before {
    left: 100%;
}

.btn:active {
    transform: scale(0.98);
}

.btn-primary {
    background: linear-gradient(135deg, var(--primary-600) 0%, var(--primary-700) 100%);
    color: white;
    box-shadow: var(--shadow-primary);
}

.btn-primary:hover {
    background: linear-gradient(135deg, var(--primary-700) 0%, var(--primary-800) 100%);
    transform: translateY(-2px);
    box-shadow: var(--shadow-lg), var(--shadow-primary);
}

.btn-secondary {
    background: linear-gradient(135deg, var(--secondary-600) 0%, var(--secondary-700) 100%);
    color: white;
    box-shadow: var(--shadow-secondary);
}

.btn-success {
    background: linear-gradient(135deg, var(--success-600) 0%, var(--success-700) 100%);
    color: white;
    box-shadow: var(--shadow-success);
}

.btn-outline-primary {
    background: transparent;
    color: var(--primary-600);
    border-color: var(--primary-300);
}

.btn-outline-primary:hover {
    background: var(--primary-600);
    color: white;
    border-color: var(--primary-600);
    transform: translateY(-2px);
    box-shadow: var(--shadow-primary);
}

.btn-glass {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    color: white;
}

.btn-floating {
    position: fixed;
    bottom: 2rem;
    right: 2rem;
    width: 56px;
    height: 56px;
    border-radius: var(--radius-full);
    background: linear-gradient(135deg, var(--primary-600), var(--secondary-600));
    color: white;
    box-shadow: var(--shadow-lg), var(--shadow-primary);
    z-index: var(--z-fixed);
    padding: 0;
}

.btn-floating:hover {
    transform: scale(1.1) rotate(5deg);
    box-shadow: var(--shadow-2xl), var(--shadow-primary);
}

/* Enhanced Forms */
.form-control {
    display: block;
    width: 100%;
    padding: 0.75rem 1rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: var(--gray-700);
    background: var(--surface-primary);
    border: 2px solid var(--border-light);
    border-radius: var(--radius-lg);
    transition: all var(--transition-base);
    box-shadow: var(--shadow-xs);
}

.form-control:focus {
    outline: none;
    border-color: var(--primary-500);
    box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.1), var(--shadow-sm);
    transform: translateY(-1px);
}

.form-floating {
    position: relative;
}

.form-floating > .form-control {
    padding: 1rem 1rem 0.5rem;
}

.form-floating > label {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    padding: 1rem;
    overflow: hidden;
    text-align: start;
    text-overflow: ellipsis;
    white-space: nowrap;
    pointer-events: none;
    border: 2px solid transparent;
    transform-origin: 0 0;
    transition: all var(--transition-base);
    color: var(--gray-500);
}

.form-floating > .form-control:focus ~ label,
.form-floating > .form-control:not(:placeholder-shown) ~ label {
    color: var(--primary-600);
    transform: scale(0.85) translateY(-0.5rem) translateX(0.15rem);
    font-weight: 600;
}

/* Enhanced Navigation */
.navbar {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(20px) saturate(180%);
    -webkit-backdrop-filter: blur(20px) saturate(180%);
    border-bottom: 1px solid var(--border-light);
    box-shadow: var(--shadow-sm);
    transition: all var(--transition-base);
}

.navbar-brand {
    font-weight: 800;
    font-size: 1.5rem;
    background: linear-gradient(135deg, var(--primary-600), var(--secondary-600));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.nav-link {
    font-weight: 500;
    color: var(--gray-700);
    padding: 0.5rem 1rem;
    border-radius: var(--radius-md);
    transition: all var(--transition-base);
    position: relative;
}

.nav-link::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, var(--primary-600), var(--secondary-600));
    transition: all var(--transition-base);
    transform: translateX(-50%);
}

.nav-link:hover {
    color: var(--primary-600);
    background: var(--primary-50);
}

.nav-link:hover::after {
    width: 80%;
}

.nav-link.active {
    color: var(--primary-600);
    background: var(--primary-100);
}

.nav-link.active::after {
    width: 80%;
}

/* Enhanced Sidebar */
.sidebar {
    background: linear-gradient(180deg, var(--surface-primary) 0%, var(--surface-secondary) 100%);
    border-right: 1px solid var(--border-light);
    box-shadow: var(--shadow-sm);
    min-height: 100vh;
}

.sidebar .nav-link {
    margin: 0.25rem 0.75rem;
    padding: 0.75rem 1rem;
    border-radius: var(--radius-lg);
    color: var(--gray-700);
    font-weight: 500;
    transition: all var(--transition-base);
    position: relative;
    overflow: hidden;
}

.sidebar .nav-link::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(99, 102, 241, 0.1), transparent);
    transition: left var(--transition-slow);
}

.sidebar .nav-link:hover {
    color: var(--primary-700);
    background: var(--primary-50);
    transform: translateX(4px);
}

.sidebar .nav-link:hover::before {
    left: 100%;
}

.sidebar .nav-link.active {
    color: white;
    background: linear-gradient(135deg, var(--primary-600) 0%, var(--primary-700) 100%);
    box-shadow: var(--shadow-primary);
}

.sidebar .nav-link i {
    width: 1.25rem;
    margin-right: 0.75rem;
}

/* Enhanced Video Components */
.video-card {
    background: var(--surface-primary);
    border: 1px solid var(--border-light);
    border-radius: var(--radius-xl);
    overflow: hidden;
    transition: all var(--transition-base);
    box-shadow: var(--shadow-sm);
    position: relative;
}

.video-card::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(217, 70, 239, 0.1) 100%);
    opacity: 0;
    transition: opacity var(--transition-base);
    pointer-events: none;
}

.video-card:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: var(--shadow-2xl);
    border-color: var(--primary-200);
}

.video-card:hover::after {
    opacity: 1;
}

.video-thumbnail {
    position: relative;
    aspect-ratio: 16/9;
    overflow: hidden;
    background: var(--gray-900);
}

.video-thumbnail img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform var(--transition-slow);
}

.video-card:hover .video-thumbnail img {
    transform: scale(1.1) rotate(1deg);
}

.video-overlay {
    position: absolute;
    inset: 0;
    background: linear-gradient(135deg, rgba(0, 0, 0, 0.4) 0%, rgba(0, 0, 0, 0.7) 100%);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: all var(--transition-base);
    backdrop-filter: blur(2px);
}

.video-card:hover .video-overlay {
    opacity: 1;
}

.video-play-button {
    width: 64px;
    height: 64px;
    background: rgba(255, 255, 255, 0.9);
    border-radius: var(--radius-full);
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--primary-600);
    font-size: 1.5rem;
    transition: all var(--transition-base);
    backdrop-filter: blur(10px);
}

.video-overlay:hover .video-play-button {
    transform: scale(1.1);
    background: white;
}

.video-duration {
    position: absolute;
    bottom: 0.5rem;
    right: 0.5rem;
    background: rgba(0, 0, 0, 0.8);
    color: white;
    padding: 0.25rem 0.5rem;
    border-radius: var(--radius-sm);
    font-size: 0.75rem;
    font-weight: 600;
    backdrop-filter: blur(10px);
}

.video-price {
    position: absolute;
    top: 0.5rem;
    right: 0.5rem;
    background: linear-gradient(135deg, var(--success-500) 0%, var(--success-600) 100%);
    color: white;
    padding: 0.25rem 0.75rem;
    border-radius: var(--radius-full);
    font-size: 0.75rem;
    font-weight: 700;
    box-shadow: var(--shadow-success);
}

/* Enhanced Statistics Cards */
.stat-card {
    background: linear-gradient(135deg, var(--surface-primary) 0%, var(--surface-secondary) 100%);
    border: 1px solid var(--border-light);
    border-radius: var(--radius-xl);
    padding: 2rem;
    transition: all var(--transition-base);
    box-shadow: var(--shadow-sm);
    position: relative;
    overflow: hidden;
}

.stat-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 4px;
    background: linear-gradient(90deg, var(--primary-500), var(--secondary-500));
    transform: scaleX(0);
    transition: transform var(--transition-base);
    transform-origin: left;
}

.stat-card:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-xl);
}

.stat-card:hover::before {
    transform: scaleX(1);
}

.stat-icon {
    width: 4rem;
    height: 4rem;
    border-radius: var(--radius-xl);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    margin-bottom: 1rem;
    background: linear-gradient(135deg, var(--primary-100) 0%, var(--primary-200) 100%);
    color: var(--primary-700);
    box-shadow: var(--shadow-sm);
}

.stat-value {
    font-size: 2.5rem;
    font-weight: 800;
    line-height: 1;
    background: linear-gradient(135deg, var(--primary-600), var(--secondary-600));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.stat-label {
    color: var(--gray-600);
    font-weight: 500;
    font-size: 0.875rem;
    margin-top: 0.5rem;
}



/* Enhanced Tables */
.table {
    border-radius: var(--radius-xl);
    overflow: hidden;
    box-shadow: var(--shadow-sm);
    background: var(--surface-primary);
}

.table thead th {
    background: linear-gradient(135deg, var(--gray-800) 0%, var(--gray-900) 100%);
    color: white;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    font-size: 0.75rem;
    padding: 1rem;
    border: none;
}

.table tbody tr {
    transition: all var(--transition-base);
    border-bottom: 1px solid var(--border-light);
}

.table tbody tr:hover {
    background: var(--primary-50);
    transform: scale(1.01);
}

.table tbody td {
    padding: 1rem;
    vertical-align: middle;
}

/* Enhanced Badges */
.badge {
    padding: 0.375rem 0.75rem;
    font-size: 0.75rem;
    font-weight: 600;
    border-radius: var(--radius-full);
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.badge-primary {
    background: linear-gradient(135deg, var(--primary-500) 0%, var(--primary-600) 100%);
    color: white;
    box-shadow: var(--shadow-primary);
}

.badge-success {
    background: linear-gradient(135deg, var(--success-500) 0%, var(--success-600) 100%);
    color: white;
    box-shadow: var(--shadow-success);
}

.badge-warning {
    background: linear-gradient(135deg, var(--warning-500) 0%, var(--warning-600) 100%);
    color: white;
    box-shadow: var(--shadow-warning);
}

.badge-danger {
    background: linear-gradient(135deg, var(--danger-500) 0%, var(--danger-600) 100%);
    color: white;
    box-shadow: var(--shadow-danger);
}

/* Enhanced Progress Bars */
.progress {
    height: 0.75rem;
    background: var(--gray-200);
    border-radius: var(--radius-full);
    overflow: hidden;
    box-shadow: var(--shadow-inner);
}

.progress-bar {
    background: linear-gradient(90deg, var(--primary-500) 0%, var(--secondary-500) 100%);
    border-radius: var(--radius-full);
    transition: width var(--transition-slow);
    position: relative;
    overflow: hidden;
}

.progress-bar::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    animation: progress-shimmer 2s infinite;
}

@keyframes progress-shimmer {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
}

/* Enhanced Alerts */
.alert {
    border: none;
    border-radius: var(--radius-xl);
    padding: 1.5rem;
    box-shadow: var(--shadow-sm);
    position: relative;
    overflow: hidden;
}

.alert::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 4px;
    height: 100%;
    background: currentColor;
}

.alert-primary {
    background: linear-gradient(135deg, var(--primary-50) 0%, var(--primary-100) 100%);
    color: var(--primary-800);
    border-left: 4px solid var(--primary-500);
}

.alert-success {
    background: linear-gradient(135deg, var(--success-50) 0%, var(--success-100) 100%);
    color: var(--success-800);
    border-left: 4px solid var(--success-500);
}

.alert-warning {
    background: linear-gradient(135deg, var(--warning-50) 0%, var(--warning-100) 100%);
    color: var(--warning-800);
    border-left: 4px solid var(--warning-500);
}

.alert-danger {
    background: linear-gradient(135deg, var(--danger-50) 0%, var(--danger-100) 100%);
    color: var(--danger-800);
    border-left: 4px solid var(--danger-500);
}

/* Advanced Animations */
@keyframes float {
    0%, 100% { transform: translateY(0) rotate(0deg); }
    25% { transform: translateY(-10px) rotate(1deg); }
    50% { transform: translateY(-5px) rotate(0deg); }
    75% { transform: translateY(-15px) rotate(-1deg); }
}

@keyframes pulse-glow {
    0%, 100% { box-shadow: 0 0 5px rgba(99, 102, 241, 0.5), 0 0 10px rgba(99, 102, 241, 0.3), 0 0 15px rgba(99, 102, 241, 0.2); }
    50% { box-shadow: 0 0 20px rgba(99, 102, 241, 0.6), 0 0 30px rgba(99, 102, 241, 0.4), 0 0 40px rgba(99, 102, 241, 0.3); }
}

@keyframes slide-in-up {
    from {
        opacity: 0;
        transform: translateY(30px) scale(0.9);
    }
    to {
        opacity: 1;
        transform: translateY(0) scale(1);
    }
}

@keyframes fade-in-scale {
    from {
        opacity: 0;
        transform: scale(0.8);
    }
    to {
        opacity: 1;
        transform: scale(1);
    }
}

/* Animation Classes */
.animate-float { animation: float 6s ease-in-out infinite; }
.animate-pulse-glow { animation: pulse-glow 3s ease-in-out infinite; }
.animate-slide-in-up { animation: slide-in-up 0.6s cubic-bezier(0.4, 0, 0.2, 1); }
.animate-fade-in-scale { animation: fade-in-scale 0.5s cubic-bezier(0.4, 0, 0.2, 1); }

/* Utility Classes */
.glass {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(20px) saturate(180%);
    -webkit-backdrop-filter: blur(20px) saturate(180%);
    border: 1px solid rgba(255, 255, 255, 0.2);
}

.gradient-text {
    background: linear-gradient(135deg, var(--primary-600), var(--secondary-600));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    font-weight: 700;
}

.hover-lift {
    transition: transform var(--transition-base);
}

.hover-lift:hover {
    transform: translateY(-4px);
}

.hover-scale {
    transition: transform var(--transition-base);
}

.hover-scale:hover {
    transform: scale(1.05);
}

.hover-rotate {
    transition: transform var(--transition-base);
}

.hover-rotate:hover {
    transform: rotate(5deg);
}

/* Custom Scrollbar */
::-webkit-scrollbar {
    width: 8px;
    height: 8px;
}

::-webkit-scrollbar-track {
    background: var(--gray-100);
    border-radius: var(--radius-full);
}

::-webkit-scrollbar-thumb {
    background: linear-gradient(135deg, var(--primary-500), var(--secondary-500));
    border-radius: var(--radius-full);
    transition: background var(--transition-base);
}

::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(135deg, var(--primary-600), var(--secondary-600));
}

/* Responsive Design */
@media (max-width: 768px) {
    .btn-floating {
        bottom: 1rem;
        right: 1rem;
        width: 48px;
        height: 48px;
    }

    .video-card:hover {
        transform: none;
    }

    .stat-card {
        padding: 1.5rem;
    }

    .modal-header,
    .modal-body {
        padding: 1.5rem;
    }
}

/* Dark Mode Support */
@media (prefers-color-scheme: dark) {
    :root {
        --surface-primary: var(--gray-900);
        --surface-secondary: var(--gray-800);
        --surface-tertiary: var(--gray-700);
    }

    body {
        background: linear-gradient(135deg, var(--gray-900) 0%, var(--gray-800) 100%);
        color: var(--gray-100);
    }

    .card {
        background: var(--gray-800);
        border-color: var(--gray-700);
        color: var(--gray-100);
    }

    .navbar {
        background: rgba(17, 24, 39, 0.95);
        border-bottom-color: var(--gray-700);
    }

    .sidebar {
        background: linear-gradient(180deg, var(--gray-900) 0%, var(--gray-800) 100%);
        border-right-color: var(--gray-700);
    }
}

/* Print Styles */
@media print {
    .sidebar,
    .navbar,
    .btn-floating,
    .modal {
        display: none !important;
    }

    .card {
        box-shadow: none !important;
        border: 1px solid var(--gray-300) !important;
        break-inside: avoid;
    }

    body {
        background: white !important;
        color: black !important;
    }
}

/* Focus States for Accessibility */
.btn:focus,
.form-control:focus,
.nav-link:focus {
    outline: 2px solid var(--primary-500);
    outline-offset: 2px;
}

/* High Contrast Mode */
@media (prefers-contrast: high) {
    .card {
        border: 2px solid currentColor;
    }

    .btn {
        border: 2px solid currentColor;
    }
}

/* Reduced Motion */
@media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

/* Loading States */
.skeleton {
    background: linear-gradient(90deg, var(--gray-200) 25%, var(--gray-300) 50%, var(--gray-200) 75%);
    background-size: 200% 100%;
    animation: skeleton-loading 1.5s infinite;
    border-radius: var(--radius-md);
}

@keyframes skeleton-loading {
    0% { background-position: 200% 0; }
    100% { background-position: -200% 0; }
}

.loading-overlay {
    position: absolute;
    inset: 0;
    background: rgba(255, 255, 255, 0.9);
    backdrop-filter: blur(4px);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: var(--z-modal);
    border-radius: inherit;
}

.spinner {
    width: 2rem;
    height: 2rem;
    border: 3px solid var(--gray-200);
    border-top: 3px solid var(--primary-500);
    border-radius: var(--radius-full);
    animation: spin 1s linear infinite;
}

@keyframes spin {
    to { transform: rotate(360deg); }
}

assets/js/admin.js.content:
/**
 * VideoHub Admin Panel Module
 * Handles admin dashboard functionality with API integration
 */

class AdminManager {
    constructor() {
        console.log('AdminManager constructor called');

        // Prevent multiple instances
        if (window.adminManagerInstance) {
            console.log('Returning existing AdminManager instance');
            return window.adminManagerInstance;
        }

        this.users = [];
        this.videos = [];
        this.stats = {};
        this.usersTable = null;
        this.currentUserId = null;
        this.isInitializingTable = false;
        this.dataLoaded = false;
        this.tableInitialized = false;
        this.handlersLoaded = false;

        // Store instance globally
        window.adminManagerInstance = this;
        console.log('New AdminManager instance created');

        this.init();
    }

    async init() {
        this.bindEvents();

        // Load page-specific data
        const currentPage = window.location.pathname.split('/').pop();
        if (currentPage === 'dashboard.html') {
            await this.loadDashboardData();
        } else if (currentPage === 'users.html') {
            // For users page, only initialize table once
            await this.initUsersPage();
        } else {
            await this.waitForAPIService();
        }

        this.loadPageSpecificHandlers();
    }

    async waitForAPIService() {
        let retries = 0;
        const maxRetries = 50;

        while (retries < maxRetries && !window.apiService) {
            await new Promise(resolve => setTimeout(resolve, 100));
            retries++;
        }

        // Only load data for non-dashboard and non-users pages
        const currentPage = window.location.pathname.split('/').pop();

        if (window.apiService && !this.dataLoaded && currentPage !== 'users.html') {
            try {
                if (currentPage === 'user-detail.html') {
                    const apiUrl = window.videoHubConfig ? window.videoHubConfig.getApiUrl() : '/api';
                    const usersResponse = await window.apiService.get(`${apiUrl}/admin/users`);
                    this.users = usersResponse.data || usersResponse.users || [];
                    this.dataLoaded = true;
                }

                if (currentPage === 'videos.html') {
                    // const videosResponse = await window.apiService.get('/videos');
                    // this.videos = videosResponse.data || videosResponse.videos || [];
                    this.dataLoaded = true;
                }
            } catch (error) {
                console.error('Failed to load data from API:', error);
                this.users = [];
                this.videos = [];
            }
        }
    }

    async loadDashboardData() {
        // Prevent multiple concurrent loads using global state
        if (window.VideoHubState && window.VideoHubState.isLoading('adminData')) {
            console.log('Admin data loading already in progress, skipping...');
            return;
        }

        try {
            if (window.VideoHubState) {
                window.VideoHubState.setLoading('adminData', true);
            }

            // Show section loaders
            const metricsSection = document.querySelector('.admin-metrics');
            const usersSection = document.querySelector('.admin-users');
            const videosSection = document.querySelector('.admin-videos');

            if (window.commonUtils) {
                if (metricsSection) window.commonUtils.showSectionLoader(metricsSection, 'Loading admin metrics...');
                if (usersSection) window.commonUtils.showSectionLoader(usersSection, 'Loading user data...');
                if (videosSection) window.commonUtils.showSectionLoader(videosSection, 'Loading video data...');
            }

            // Wait for API service to be available
            let retries = 0;
            const maxRetries = 50;

            while (retries < maxRetries && !window.apiService) {
                await new Promise(resolve => setTimeout(resolve, 100));
                retries++;
            }

            if (window.apiService) {
                // Load admin metrics from new metrics API
                try {
                    const metricsResponse = await window.apiService.get('/api/endpoints/metrics.php?type=admin');
                    if (metricsResponse.success) {
                        const metrics = metricsResponse.data;
                        this.updateDashboardMetrics(metrics);
                    } else {
                        // Calculate metrics manually if endpoint fails
                        this.calculateMetricsManually();
                    }
                } catch (error) {
                    console.log('Metrics endpoint not available, calculating manually');
                    this.calculateMetricsManually();
                }

                // Load additional data for dashboard
                const [usersResponse, videosResponse] = await Promise.all([
                    window.apiService.get('/api/admin/users'),
                    window.apiService.get('/api/videos')
                ]);

                this.users = usersResponse.data || usersResponse.users || [];
                this.videos = videosResponse.data || videosResponse.videos || [];

                // Update sidebar badges
                this.updateSidebarBadges();
            }
        } catch (error) {
            console.error('Failed to load dashboard data:', error);

            // Handle API error with proper user feedback
            if (window.commonUtils) {
                window.commonUtils.handleAPIError(error, 'Loading admin dashboard data');
            }

            // Set empty values on error
            this.updateDashboardMetrics({
                totalUsers: 0,
                totalVideos: 0,
                totalViews: 0,
                pendingVideos: 0
            });
        } finally {
            if (window.VideoHubState) {
                window.VideoHubState.setLoading('adminData', false);
            }

            // Hide section loaders
            const metricsSection = document.querySelector('.admin-metrics');
            const usersSection = document.querySelector('.admin-users');
            const videosSection = document.querySelector('.admin-videos');

            if (window.commonUtils) {
                if (metricsSection) window.commonUtils.hideSectionLoader(metricsSection);
                if (usersSection) window.commonUtils.hideSectionLoader(usersSection);
                if (videosSection) window.commonUtils.hideSectionLoader(videosSection);
            }
        }
    }

    updateDashboardMetrics(metrics) {
        // Update dashboard metric displays
        const totalUsersEl = document.getElementById('totalUsers');
        const totalVideosEl = document.getElementById('totalVideos');
        const totalViewsEl = document.getElementById('totalViews');
        const pendingVideosEl = document.getElementById('pendingVideos');

        if (totalUsersEl) totalUsersEl.textContent = metrics.totalUsers || 0;
        if (totalVideosEl) totalVideosEl.textContent = metrics.totalVideos || 0;
        if (totalViewsEl) totalViewsEl.textContent = metrics.totalViews || 0;
        if (pendingVideosEl) pendingVideosEl.textContent = metrics.pendingVideos || 0;
    }

    calculateMetricsManually() {
        // Calculate metrics from loaded data
        const metrics = {
            totalUsers: this.users.length || 0,
            totalVideos: this.videos.length || 0,
            totalViews: this.videos.reduce((sum, video) => sum + (parseInt(video.views) || 0), 0),
            pendingVideos: this.videos.filter(video => video.status === 'pending').length || 0
        };
        this.updateDashboardMetrics(metrics);
    }

    // Add the missing viewUserDetailsPage function
    viewUserDetailsPage(userId) {
        window.location.href = `user-detail.html?id=${userId}`;
    }

    updateSidebarBadges() {
        // Sidebar badges removed for cleaner interface
    }

    bindEvents() {
        // Event listeners for admin functionality
        document.addEventListener('DOMContentLoaded', () => {
            this.loadPageSpecificHandlers();
            this.bindModalEvents();
        });
    }

    bindModalEvents() {
        // Add User Modal
        const saveUserBtn = document.getElementById('saveUser');
        if (saveUserBtn) {
            saveUserBtn.addEventListener('click', () => this.handleAddUser());
        }

        // Edit User Modal
        const updateUserBtn = document.getElementById('updateUser');
        if (updateUserBtn) {
            updateUserBtn.addEventListener('click', () => this.handleUpdateUser());
        }

        // Confirm Delete
        const confirmDeleteBtn = document.getElementById('confirmDeleteBtn');
        if (confirmDeleteBtn) {
            confirmDeleteBtn.addEventListener('click', () => this.handleConfirmDelete());
        }
    }

    loadPageSpecificHandlers() {
        const currentPage = window.location.pathname.split('/').pop();

        // Prevent multiple handler loads
        if (this.handlersLoaded) {
            return;
        }
        this.handlersLoaded = true;

        switch (currentPage) {
            case 'dashboard.html':
                this.initDashboard();
                break;
            case 'users.html':
                // Users page already initialized in init() method
                break;
            case 'videos.html':
                ///this.initVideosPage();
                break;
        }
    }

    initDashboard() {
        console.log('Admin dashboard initialized');
    }

    async initUsersPage() {
        // Wait for API service first
        let retries = 0;
        while (retries < 50 && !window.apiService) {
            await new Promise(resolve => setTimeout(resolve, 100));
            retries++;
        }

        // Only initialize if not already done and ensure single initialization
        if (!this.isInitializingTable && !this.usersTable && !this.tableInitialized) {
            this.tableInitialized = true;
            await this.loadUsersDataTable();
        }
    }

    // initVideosPage() {
    //     this.loadVideosGrid();
    // }

    async loadUsersDataTable() {
        // Prevent multiple simultaneous initializations
        if (this.isInitializingTable) {
            console.log('Table initialization already in progress');
            return;
        }

        this.isInitializingTable = true;

        try {
            // Wait for jQuery and DataTables to be available
            // if (typeof $ === 'undefined' || !$.fn.DataTable) {
            //     setTimeout(() => {
            //         this.isInitializingTable = false;
            //         this.loadUsersDataTable();
            //     }, 100);
            //     return;
            // }

            // Check if table element exists
            const tableElement = $('#usersTable');
            if (tableElement.length === 0) {
                console.error('Users table element not found');
                this.isInitializingTable = false;
                return;
            }

            // Load users data first (only if not already loaded)
            if (!this.users.length && !this.dataLoaded) {
                try {
                    console.log('Loading users data from API...');
                    const apiUrl = window.videoHubConfig ? window.videoHubConfig.getApiUrl() : '/api';
                    const response = await fetch(`${apiUrl}/admin/users`);
                    const result = await response.json();
                    if (result.success) {
                        this.users = result.data || [];
                        this.dataLoaded = true;
                        console.log(`Loaded ${this.users.length} users`);
                    } else {
                        this.users = [];
                    }
                } catch (error) {
                    console.error('Failed to load users:', error);
                    this.users = [];
                }
            }

            // Update total users count
            const totalUsersCount = document.getElementById('totalUsersCount');
            if (totalUsersCount) {
                totalUsersCount.textContent = this.users.length;
            }

            // Properly destroy existing DataTable if it exists
            if ($.fn.DataTable.isDataTable('#usersTable')) {
                console.log('Destroying existing DataTable');
                tableElement.DataTable().clear().destroy();
                tableElement.empty(); // Clear table contents
                this.usersTable = null;
            }

            // Small delay to ensure cleanup is complete
            await new Promise(resolve => setTimeout(resolve, 150));

        // Initialize DataTable
        this.usersTable = tableElement.DataTable({
            data: this.users,
            responsive: true,
            pageLength: 25,
            order: [[0, 'asc']],
            columnDefs: [
                { 
                    targets: 0,
                    data: null,
                    width: '60px',
                    className: 'text-center',
                    orderable: false,
                    render: function(data, type, row, meta) {
                        return meta.row + 1; // Serial number starting from 1
                    }
                },
                { 
                    targets: 1,
                    data: null,
                    orderable: true,
                    render: function(data, type, row) {
                        const name = row.name || 'Unknown User';
                        const initials = name.split(' ').map(n => n[0]).join('').toUpperCase().substring(0, 2);
                        return `
                            <div class="d-flex align-items-center">
                                <div class="user-avatar me-3">
                                    ${initials}
                                </div>
                                <div>
                                    <div class="fw-semibold">${name}</div>
                                    <small class="text-muted">ID: ${row.id}</small>
                                </div>
                            </div>
                        `;
                    }
                },
                { 
                    targets: 2,
                    data: 'email',
                    render: function(data) {
                        return data || 'No email';
                    }
                },
                { 
                    targets: 3,
                    data: 'role',
                    render: function(data) {
                        const badgeClass = {
                            'admin': 'danger',
                            'creator': 'success', 
                            'viewer': 'primary'
                        };
                        const roleText = data ? data.charAt(0).toUpperCase() + data.slice(1) : 'Viewer';
                        return `<span class="badge bg-${badgeClass[data] || 'secondary'}">${roleText}</span>`;
                    }
                },
                {
                    targets: 4,
                    data: 'status',
                    render: function(data) {
                        const badgeClass = {
                            'active': 'success',
                            'inactive': 'warning',
                            'suspended': 'danger',
                            'revoked': 'dark'
                        };
                        const statusText = data ? data.charAt(0).toUpperCase() + data.slice(1) : 'Active';
                        return `<span class="badge bg-${badgeClass[data] || 'success'}">${statusText}</span>`;
                    }
                },
                {
                    targets: 5,
                    data: null,
                    render: function(data, type, row) {
                        return row.email_verified ? '<span class="badge bg-success">Verified</span>' : '<span class="badge bg-warning">Not Verified</span>';
                    }
                },
                {
                    targets: 6,
                    data: 'joinDate',
                    render: function(data) {
                        if (!data) return 'Unknown';
                        try {
                            return new Date(data).toLocaleDateString();
                        } catch (e) {
                            return data;
                        }
                    }
                },
                {
                    targets: 7,
                    data: null,
                    orderable: false,
                    width: '200px',
                    className: 'text-center',
                    render: function(data, type, row) {
                        return `
                            <div class="btn-group" role="group">
                                <div class="dropdown">
                                    <button class="btn btn-sm btn-outline-info dropdown-toggle" type="button" data-bs-toggle="dropdown" title="View Options">
                                        <i class="fas fa-eye"></i>
                                    </button>
                                    <ul class="dropdown-menu">
                                        <li><a class="dropdown-item" href="#" onclick="adminManager.showUserDetails(${row.id})"><i class="fas fa-info-circle me-2"></i>Quick View</a></li>
                                        <li><a class="dropdown-item" href="#" onclick="viewUserDetailsPage(${row.id})"><i class="fas fa-external-link-alt me-2"></i>Full Details</a></li>
                                    </ul>
                                </div>
                                <button class="btn btn-sm btn-outline-primary" onclick="adminManager.editUser(${row.id})" title="Edit User">
                                    <i class="fas fa-edit"></i>
                                </button>
                                <button class="btn btn-sm btn-outline-warning" onclick="revokeUser(${row.id})" title="Revoke Access">
                                    <i class="fas fa-ban"></i>
                                </button>
                                <button class="btn btn-sm btn-outline-danger" onclick="deleteUser(${row.id})" title="Delete User">
                                    <i class="fas fa-trash"></i>
                                </button>
                            </div>
                        `;
                    }
                }
            ],
            language: {
                search: "Search users:",
                lengthMenu: "Show _MENU_ users per page",
                info: "Showing _START_ to _END_ of _TOTAL_ users",
                infoEmpty: "No users available",
                infoFiltered: "(filtered from _MAX_ total users)",
                zeroRecords: "No matching users found",
                paginate: {
                    first: "First",
                    last: "Last",
                    next: "Next",
                    previous: "Previous"
                }
            },
            dom: '<"row"<"col-sm-12 col-md-6"l><"col-sm-12 col-md-6"f>>' +
                 '<"row"<"col-sm-12"tr>>' +
                 '<"row"<"col-sm-12 col-md-5"i><"col-sm-12 col-md-7"p>>',
            searchDelay: 400,
            processing: true,
            drawCallback: function() {
                console.log('DataTable draw completed');
            }
        });

        console.log('DataTable initialized successfully with', this.users.length, 'users');

        // Hide loading indicator after successful initialization
        const loadingIndicator = document.getElementById('usersLoadingIndicator');
        if (loadingIndicator) {
            loadingIndicator.style.display = 'none';
        }

        } catch (error) {
            console.error('Error initializing DataTable:', error);
            // Hide loading indicator even on error
            const loadingIndicator = document.getElementById('usersLoadingIndicator');
            if (loadingIndicator) {
                loadingIndicator.style.display = 'none';
            }
        } finally {
            this.isInitializingTable = false;
        }
    }

    // User management methods
    async showUserDetails(userId) {
        try {
            // Find user in local data first
            let user = this.users.find(u => u.id == userId);

            // If not found locally, fetch from API
            if (!user) {
                const response = await fetch(`/api/endpoints/users.php/${userId}`);
                const result = await response.json();
                if (result.success) {
                    user = result.data;
                }
            }

            if (user) {
                // Helper function to safely set element content
                const safeSetContent = (elementId, content) => {
                    const element = document.getElementById(elementId);
                    if (element) {
                        element.textContent = content;
                    } else {
                        console.warn(`Element with ID '${elementId}' not found`);
                    }
                };

                const safeSetClass = (elementId, className) => {
                    const element = document.getElementById(elementId);
                    if (element) {
                        element.className = className;
                    } else {
                        console.warn(`Element with ID '${elementId}' not found`);
                    }
                };

                // Populate user details modal safely
                safeSetContent('detailUserId', user.id);
                safeSetContent('detailUserName', user.name || 'Unknown User');
                safeSetContent('detailUserEmail', user.email || 'No email');
                safeSetContent('detailUserRole', (user.role || 'viewer').charAt(0).toUpperCase() + (user.role || 'viewer').slice(1));
                safeSetClass('detailUserRole', `badge bg-${this.getUserRoleBadgeClass(user.role)}`);

                // Status badge
                const statusEl = document.getElementById('detailUserStatus');
                if (statusEl) {
                    statusEl.textContent = (user.status || 'active').charAt(0).toUpperCase() + (user.status || 'active').slice(1);
                    statusEl.className = `badge bg-${user.status === 'active' ? 'success' : user.status === 'suspended' ? 'danger' : user.status === 'revoked' ? 'dark' : 'warning'}`;
                }

                safeSetContent('detailJoinDate', user.joinDate ? new Date(user.joinDate).toLocaleDateString() : (user.created_at ? new Date(user.created_at).toLocaleDateString() : 'Unknown'));
                safeSetContent('detailLastSeen', user.updated_at ? new Date(user.updated_at).toLocaleDateString() : 'Recently');

                const emailVerifiedEl = document.getElementById('detailEmailVerified');
                if (emailVerifiedEl) {
                    emailVerifiedEl.textContent = user.email_verified_at ? 'Verified' : 'Not Verified';
                    emailVerifiedEl.className = `badge bg-${user.email_verified_at ? 'success' : 'warning'}`;
                }

                // Optional elements that might not exist on all pages
                safeSetContent('detailLastLogin', 'Recently'); // Mock data for now
