/**
 * VideoHub Stripe Payment Module
 * Handles real Stripe payment processing for video purchases - Stripe ONLY
 */

class StripePaymentManager {
    constructor() {
        this.stripe = null;
        this.elements = null;
        this.cardElement = null;
        this.currentPaymentIntent = null;
        this.publishableKey = null;
        this.isInitialized = false;
        this.init();
    }

    async init() {
        try {
            await this.loadStripeKey();
            this.initializeStripe();
            this.bindEvents();
            this.isInitialized = true;
        } catch (error) {
            console.error('Failed to initialize Stripe:', error);
            this.showError('Payment system initialization failed');
        }
    }

    async loadStripeKey() {
        try {
            const response = await fetch(this.getApiUrl('/config/stripe-key'));
            if (response.ok) {
                const data = await response.json();
                this.publishableKey = data.publishable_key;
            } else {
                throw new Error('Failed to load Stripe key');
            }
        } catch (error) {
            console.error('Could not load Stripe key from API:', error);
            throw error;
        }
    }

    initializeStripe() {
        if (!this.publishableKey) {
            throw new Error('Stripe publishable key not found');
        }

        this.stripe = Stripe(this.publishableKey);
        this.elements = this.stripe.elements();

        const style = {
            base: {
                fontSize: '16px',
                color: '#424770',
                '::placeholder': {
                    color: '#aab7c4',
                },
            },
            invalid: {
                color: '#9e2146',
            },
        };

        this.cardElement = this.elements.create('card', {style: style});
    }

    bindEvents() {
        document.addEventListener('click', (e) => {
            if (e.target.classList.contains('purchase-video-btn')) {
                e.preventDefault();
                this.handlePurchaseClick(e.target);
            }
        });

        document.addEventListener('submit', (e) => {
            if (e.target.id === 'stripe-payment-form') {
                e.preventDefault();
                this.processStripePayment(e.target);
            }
        });
    }

    async handlePurchaseClick(button) {
        if (!this.isInitialized) {
            this.showError('Payment system is not ready. Please refresh the page.');
            return;
        }

        const videoId = button.dataset.videoId;
        const videoTitle = button.dataset.videoTitle;
        const videoPrice = parseFloat(button.dataset.videoPrice);

        if (!videoId || !videoPrice) {
            this.showError('Invalid video information');
            return;
        }

        try {
            const userId = this.getCurrentUserId();
            if (!userId) {
                this.showError('Please log in to purchase videos');
                return;
            }

            this.showStripePaymentModal(videoId, videoTitle, videoPrice);
        } catch (error) {
            console.error('Error initiating purchase:', error);
            this.showError('Failed to start purchase process');
        }
    }

    showStripePaymentModal(videoId, videoTitle, videoPrice) {
        const modalHtml = `
            <div class="modal fade" id="stripePaymentModal" tabindex="-1" aria-labelledby="stripePaymentModalLabel" aria-hidden="true">
                <div class="modal-dialog modal-dialog-centered">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="stripePaymentModalLabel">
                                <i class="fas fa-credit-card me-2"></i>Purchase Video
                            </h5>
                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div class="modal-body">
                            <div class="mb-3">
                                <h6>${videoTitle}</h6>
                                <div class="text-end">
                                    <span class="h4 text-primary">$${videoPrice.toFixed(2)}</span>
                                    <small class="text-muted d-block">One-time purchase</small>
                                </div>
                            </div>

                            <form id="stripe-payment-form">
                                <div class="mb-3">
                                    <label class="form-label">Card Details</label>
                                    <div id="card-element" class="form-control" style="height: 45px; padding: 10px;"></div>
                                    <div id="card-errors" class="text-danger mt-2" style="display: none;"></div>
                                </div>

                                <input type="hidden" id="video-id" value="${videoId}">
                                <input type="hidden" id="video-price" value="${videoPrice}">

                                <button type="submit" id="submit-payment" class="btn btn-primary w-100" disabled>
                                    <span class="spinner-border spinner-border-sm me-2" style="display: none;"></span>
                                    Pay $${videoPrice.toFixed(2)}
                                </button>
                            </form>

                            <div class="mt-3 text-center">
                                <small class="text-muted">
                                    <i class="fas fa-shield-alt me-1"></i>
                                    Secured by Stripe
                                </small>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        `;

        const existingModal = document.getElementById('stripePaymentModal');
        if (existingModal) {
            existingModal.remove();
        }

        document.body.insertAdjacentHTML('beforeend', modalHtml);

        const modal = new bootstrap.Modal(document.getElementById('stripePaymentModal'));
        modal.show();

        setTimeout(() => {
            this.mountCardElement();
        }, 300);
    }

    mountCardElement() {
        const cardElementContainer = document.getElementById('card-element');
        const cardErrors = document.getElementById('card-errors');
        const submitButton = document.getElementById('submit-payment');

        if (!cardElementContainer || !this.cardElement) {
            console.error('Card element container not found or Stripe not initialized');
            return;
        }

        this.cardElement.mount('#card-element');

        this.cardElement.on('change', (event) => {
            if (event.error) {
                cardErrors.textContent = event.error.message;
                cardErrors.style.display = 'block';
                submitButton.disabled = true;
            } else {
                cardErrors.style.display = 'none';
                submitButton.disabled = event.empty;
            }
        });
    }

    async processStripePayment(form) {
        const videoId = form.querySelector('#video-id').value;
        const videoPrice = parseFloat(form.querySelector('#video-price').value);
        const submitButton = form.querySelector('#submit-payment');
        const spinner = submitButton.querySelector('.spinner-border');

        submitButton.disabled = true;
        spinner.style.display = 'inline-block';

        try {
            const paymentIntentResponse = await this.createPaymentIntent(videoId, videoPrice);

            if (!paymentIntentResponse.success) {
                throw new Error(paymentIntentResponse.message);
            }

            const {error, paymentIntent} = await this.stripe.confirmCardPayment(
                paymentIntentResponse.client_secret,
                {
                    payment_method: {
                        card: this.cardElement,
                    }
                }
            );

            if (error) {
                throw new Error(error.message);
            }

            if (paymentIntent.status === 'succeeded') {
                await this.confirmPayment(paymentIntent.id, videoId);
                this.showSuccess('Payment successful! You can now watch the video.');

                bootstrap.Modal.getInstance(document.getElementById('stripePaymentModal')).hide();

                setTimeout(() => {
                    window.location.reload();
                }, 2000);
            }

        } catch (error) {
            console.error('Payment failed:', error);
            this.showError(error.message || 'Payment failed. Please try again.');
        } finally {
            submitButton.disabled = false;
            spinner.style.display = 'none';
        }
    }

    async createPaymentIntent(videoId, videoPrice) {
        const userId = this.getCurrentUserId();

        const response = await fetch(this.getApiUrl('/payments/create-payment-intent'), {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                video_id: videoId,
                user_id: userId,
                amount: videoPrice
            })
        });

        return await response.json();
    }

    async confirmPayment(paymentIntentId, videoId) {
        const userId = this.getCurrentUserId();

        const response = await fetch(this.getApiUrl('/payments/confirm-payment'), {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                payment_intent_id: paymentIntentId,
                video_id: videoId,
                user_id: userId
            })
        });

        return await response.json();
    }

    getCurrentUserId() {
        const userSession = JSON.parse(localStorage.getItem('userSession') || sessionStorage.getItem('userSession') || '{}');
        return userSession.id || userSession.userId;
    }

    getApiUrl(endpoint) {
        const config = window.videoHubConfig;
        if (config) {
            return config.getUrl(`/api${endpoint}`);
        }
        return `/api${endpoint}`;
    }

    showError(message) {
        if (window.commonUtils && window.commonUtils.showNotification) {
            window.commonUtils.showNotification(message, 'error');
        } else {
            alert('Error: ' + message);
        }
    }

    showSuccess(message) {
        if (window.commonUtils && window.commonUtils.showNotification) {
            window.commonUtils.showNotification(message, 'success');
        } else {
            alert(message);
        }
    }
}

// Initialize when DOM is ready
document.addEventListener('DOMContentLoaded', function() {
    if (window.Stripe) {
        window.stripePaymentManager = new StripePaymentManager();
    } else {
        console.error('Stripe.js not loaded');
    }
});

// Global function for backward compatibility
window.purchaseVideo = function(videoId, videoTitle, videoPrice) {
    if (window.stripePaymentManager && window.stripePaymentManager.isInitialized) {
        window.stripePaymentManager.showStripePaymentModal(videoId, videoTitle, videoPrice);
    } else {
        console.error('Stripe payment manager not initialized');
        if (window.commonUtils && window.commonUtils.showNotification) {
            window.commonUtils.showNotification('Payment system not ready. Please refresh the page.', 'error');
        }
    }
};

