/**
 * VideoHub Stripe Payment Module
 * Handles real Stripe payment processing for video purchases
 */

class StripePaymentManager {
    constructor() {
        this.stripe = null;
        this.elements = null;
        this.cardElement = null;
        this.currentPaymentIntent = null;
        this.publishableKey = null;
        this.init();
    }

    async init() {
        await this.loadStripeKey();
        this.initializeStripe();
        this.bindEvents();
    }

    async loadStripeKey() {
        try {
            // Load Stripe publishable key from environment
            const response = await fetch(this.getApiUrl('/config/stripe-key'));
            if (response.ok) {
                const data = await response.json();
                this.publishableKey = data.publishable_key;
            } else {
                // Fallback to environment variable if API endpoint doesn't exist
                this.publishableKey = window.STRIPE_PUBLISHABLE_KEY || null;
            }
        } catch (error) {
            console.warn('Could not load Stripe key from API, using fallback');
            this.publishableKey = window.STRIPE_PUBLISHABLE_KEY || null;
        }
    }

    initializeStripe() {
        if (!this.publishableKey) {
            console.error('Stripe publishable key not found');
            this.showError('Payment system is not properly configured');
            return;
        }

        // Initialize Stripe
        this.stripe = Stripe(this.publishableKey);
        this.elements = this.stripe.elements();

        // Create card element with custom styling
        const style = {
            base: {
                fontSize: '16px',
                color: '#424770',
                '::placeholder': {
                    color: '#aab7c4',
                },
            },
            invalid: {
                color: '#9e2146',
            },
        };

        this.cardElement = this.elements.create('card', {style: style});
    }

    bindEvents() {
        // Payment form submission
        document.addEventListener('submit', (e) => {
            if (e.target.id === 'stripe-payment-form' || e.target.classList.contains('stripe-payment-form')) {
                e.preventDefault();
                this.processStripePayment(e.target);
            }
        });

        // Purchase button clicks
        document.addEventListener('click', (e) => {
            if (e.target.classList.contains('purchase-video-btn')) {
                e.preventDefault();
                const videoId = e.target.dataset.videoId;
                const videoTitle = e.target.dataset.videoTitle;
                const videoPrice = parseFloat(e.target.dataset.videoPrice);
                this.initiateVideoPurchase(videoId, videoTitle, videoPrice);
            }
        });
    }

    async initiateVideoPurchase(videoId, videoTitle, videoPrice) {
        try {
            const userId = this.getCurrentUserId();
            if (!userId) {
                this.showError('Please log in to purchase videos');
                return;
            }

            // Show payment modal
            this.showPaymentModal(videoId, videoTitle, videoPrice);

        } catch (error) {
            console.error('Error initiating purchase:', error);
            this.showError('Failed to start purchase process');
        }
    }

    showPaymentModal(videoId, videoTitle, videoPrice) {
        const modalHtml = `
            <div class="modal fade" id="stripePaymentModal" tabindex="-1">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title">Purchase Video</h5>
                            <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
                        </div>
                        <div class="modal-body">
                            <div class="purchase-summary mb-4">
                                <h6>${videoTitle}</h6>
                                <p class="text-muted mb-2">One-time purchase</p>
                                <div class="d-flex justify-content-between">
                                    <span>Price:</span>
                                    <strong>$${videoPrice.toFixed(2)}</strong>
                                </div>
                            </div>

                            <form id="stripe-payment-form" class="stripe-payment-form">
                                <div class="mb-3">
                                    <label class="form-label">Card Details</label>
                                    <div id="card-element" class="form-control" style="height: 45px; padding: 10px;"></div>
                                    <div id="card-errors" class="text-danger mt-2" style="display: none;"></div>
                                </div>

                                <input type="hidden" id="video-id" value="${videoId}">
                                <input type="hidden" id="video-price" value="${videoPrice}">

                                <button type="submit" id="submit-payment" class="btn btn-primary w-100" disabled>
                                    <span class="spinner-border spinner-border-sm me-2" style="display: none;"></span>
                                    Pay $${videoPrice.toFixed(2)}
                                </button>
                            </form>

                            <div class="mt-3 text-center">
                                <small class="text-muted">Secured by Stripe</small>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        `;

        // Remove existing modal if any
        const existingModal = document.getElementById('stripePaymentModal');
        if (existingModal) {
            existingModal.remove();
        }

        // Add modal to page
        document.body.insertAdjacentHTML('beforeend', modalHtml);

        // Show modal and mount Stripe element
        const modal = new bootstrap.Modal(document.getElementById('stripePaymentModal'));
        modal.show();

        // Mount card element after modal is shown
        setTimeout(() => {
            this.mountCardElement();
        }, 300);
    }

    mountCardElement() {
        const cardElementContainer = document.getElementById('card-element');
        const cardErrors = document.getElementById('card-errors');
        const submitButton = document.getElementById('submit-payment');

        if (!cardElementContainer || !this.cardElement) {
            console.error('Card element container not found or Stripe not initialized');
            return;
        }

        // Mount the card element
        this.cardElement.mount('#card-element');

        // Handle real-time validation errors
        this.cardElement.on('change', (event) => {
            if (event.error) {
                cardErrors.textContent = event.error.message;
                cardErrors.style.display = 'block';
                submitButton.disabled = true;
            } else {
                cardErrors.style.display = 'none';
                submitButton.disabled = event.empty;
            }
        });
    }

    async processStripePayment(form) {
        const videoId = form.querySelector('#video-id').value;
        const videoPrice = parseFloat(form.querySelector('#video-price').value);
        const submitButton = form.querySelector('#submit-payment');
        const spinner = submitButton.querySelector('.spinner-border');

        // Show loading state
        submitButton.disabled = true;
        spinner.style.display = 'inline-block';

        try {
            // Step 1: Create Payment Intent
            const paymentIntentResponse = await this.createPaymentIntent(videoId, videoPrice);

            if (!paymentIntentResponse.success) {
                throw new Error(paymentIntentResponse.message);
            }

            // Step 2: Confirm payment with Stripe
            const {error, paymentIntent} = await this.stripe.confirmCardPayment(
                paymentIntentResponse.client_secret,
                {
                    payment_method: {
                        card: this.cardElement,
                        billing_details: {
                            // You can collect billing details if needed
                        }
                    }
                }
            );

            if (error) {
                throw new Error(error.message);
            }

            // Step 3: Confirm payment on backend
            await this.confirmPaymentOnBackend(paymentIntent.id);

            // Success!
            this.handlePaymentSuccess(paymentIntent);

        } catch (error) {
            console.error('Payment error:', error);
            this.handlePaymentError(error.message);
        } finally {
            // Hide loading state
            submitButton.disabled = false;
            spinner.style.display = 'none';
        }
    }

    async createPaymentIntent(videoId, amount) {
        try {
            const response = await fetch(this.getApiUrl('/payments/create-payment-intent'), {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    video_id: parseInt(videoId),
                    user_id: this.getCurrentUserId()
                })
            });

            const data = await response.json();
            return data;

        } catch (error) {
            console.error('Error creating payment intent:', error);
            return {
                success: false,
                message: 'Failed to initialize payment'
            };
        }
    }

    async confirmPaymentOnBackend(paymentIntentId) {
        try {
            const response = await fetch(this.getApiUrl('/payments/confirm-payment'), {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    payment_intent_id: paymentIntentId
                })
            });

            const data = await response.json();
            if (!data.success) {
                throw new Error(data.message);
            }

            return data;

        } catch (error) {
            console.error('Error confirming payment:', error);
            throw error;
        }
    }

    handlePaymentSuccess(paymentIntent) {
        // Close payment modal
        const modal = bootstrap.Modal.getInstance(document.getElementById('stripePaymentModal'));
        if (modal) {
            modal.hide();
        }

        // Show success message
        this.showSuccess('Payment successful! You now have access to the video.');

        // Redirect to video or refresh page
        setTimeout(() => {
            window.location.reload();
        }, 2000);
    }

    handlePaymentError(errorMessage) {
        this.showError(`Payment failed: ${errorMessage}`);
    }

    // Utility methods
    getCurrentUserId() {
        // Get current user ID from localStorage or session
        const user = JSON.parse(localStorage.getItem('user') || sessionStorage.getItem('user') || '{}');
        return user.id || null;
    }

    getApiUrl(endpoint) {
        const config = window.VIDEOHUB_DEPLOYMENT_CONFIG || { BASE_PATH: '' };
        const basePath = config.BASE_PATH || '';
        return `${basePath}/api${endpoint}`;
    }

    showError(message) {
        if (window.notificationManager) {
            window.notificationManager.error(message);
        } else {
            alert(message);
        }
    }

    showSuccess(message) {
        if (window.notificationManager) {
            window.notificationManager.success(message);
        } else {
            alert(message);
        }
    }
}

// Initialize the Stripe Payment Manager when the page loads
document.addEventListener('DOMContentLoaded', () => {
    // Only initialize if Stripe is available
    if (typeof Stripe !== 'undefined') {
        window.stripePaymentManager = new StripePaymentManager();
    } else {
        console.error('Stripe.js is not loaded. Please include it in your HTML.');
    }
});

// Backward compatibility - keep the old class name as alias
window.PaymentManager = StripePaymentManager;
